import React from 'react';

const services = [
  {
    icon: '📱',
    title: 'Social Media Management',
    desc: 'Consistent, creative, and keyword-rich content for all major platforms. Includes 16 creative posts/month, 2 reels on alternate Sundays, high-volume captions & hashtags, festive content free, and account setup if needed.',
    price: 'Starting at ₹10,500/month',
    cta: 'Explore Social Media Management →',
  },
  {
    icon: '🎨',
    title: 'Festive Post Designs',
    desc: 'Ready-to-share branded festival creatives — boost reach on special occasions. Includes logo + contact info for brand recognition.',
    price: '₹1,000/month or ₹12,000/year (10% off)',
    cta: 'View Festive Post Samples →',
  },
  {
    icon: '🎯',
    title: 'Ads Campaign Management',
    desc: 'Run powerful ads across Meta, Google & more. Free ad creatives, ₹2,000/video editing (optional), strategic ad budget management, 15% of ad budget as service fee, full transparency & reporting.',
    price: '',
    cta: 'See Ad Campaign Plans →',
  },
  {
    icon: '🌐',
    title: 'Web Development',
    desc: 'We build fast, responsive, and SEO-friendly websites for portfolios, business websites, blogs, eCommerce, and booking platforms. Custom designs + complete support.',
    price: '',
    cta: 'View Web Development Services →',
  },
  {
    icon: '📲',
    title: 'App Development',
    desc: 'Get mobile apps built by skilled developers in our freelance team. Android/iOS/Hybrid, UI/UX Design, feature-based pricing. Ideal for startups, MVPs, service businesses.',
    price: '',
    cta: 'Explore App Development →',
  },
  {
    icon: '🎞️',
    title: 'Video & Reels Editing',
    desc: 'Grow your brand with high-performing reels. 20 reels/month, captions + hashtags, free social media account handling, client provides raw footage.',
    price: '₹25,000/month or ₹2.85L/year (5% off)',
    cta: 'Learn About Reels Editing →',
  },
  {
    icon: '🧩',
    title: 'Build Your Own Plan',
    desc: "Don't need a full package? Use our custom builder tool to select services, get instant pricing, add to cart & request a callback. Smart. Transparent. Personalized.",
    price: '',
    cta: 'Customize Your Plan →',
  },
];

const Services = () => (
  <div className="bg-white py-16 px-4 sm:px-8 lg:px-24">
    <div className="max-w-4xl mx-auto mb-12 text-center">
      <h2 className="text-3xl font-bold text-primary mb-4">Our Services</h2>
      <p className="text-lg mb-6">Smart, Scalable & Freelancer-Powered Services for Every Brand</p>
    </div>
    <div className="grid gap-8 md:grid-cols-2">
      {services.map((service, idx) => (
        <div key={service.title} className="bg-light rounded-xl shadow p-8 flex flex-col justify-between">
          <div>
            <div className="text-4xl mb-2">{service.icon}</div>
            <h3 className="text-xl font-bold mb-2">{service.title}</h3>
            <p className="mb-2">{service.desc}</p>
            {service.price && <p className="font-semibold text-accent mb-2">{service.price}</p>}
          </div>
          <a href="#contact" className="mt-4 inline-block px-5 py-2 bg-primary text-white rounded-lg font-semibold shadow hover:bg-blue-800 transition">{service.cta}</a>
        </div>
      ))}
    </div>
    <div className="max-w-2xl mx-auto mt-16 text-center">
      <h3 className="text-xl font-bold mb-2">Delivered by Experts, Curated by Us</h3>
      <p className="mb-4">All services are executed by vetted freelancers from our Shyara network — ensuring quality, consistency, and creativity in every project.</p>
      <a href="#contact" className="px-6 py-3 bg-accent text-white rounded-lg font-semibold shadow hover:bg-emerald-600 transition">Book a Free Strategy Call</a>
    </div>
  </div>
);

export default Services;
