import React from 'react';
import Home from './sections/Home';
import About from './sections/About';
import Services from './sections/Services';
import Portfolio from './sections/Portfolio';
import Contact from './sections/Contact';

function App() {
  return (
    <div className="bg-light min-h-screen text-dark font-sans">
      <header className="sticky top-0 z-50 bg-white shadow">
        <nav className="container mx-auto flex items-center justify-between py-4 px-6">
          <span className="text-2xl font-bold text-primary">Shyara</span>
          <ul className="flex gap-6 text-lg">
            <li><a href="#home" className="hover:text-primary">Home</a></li>
            <li><a href="#about" className="hover:text-primary">About</a></li>
            <li><a href="#services" className="hover:text-primary">Services</a></li>
            <li><a href="#portfolio" className="hover:text-primary">Portfolio</a></li>
            <li><a href="#contact" className="hover:text-primary">Contact</a></li>
          </ul>
        </nav>
      </header>
      <main>
        <section id="home"><Home /></section>
        <section id="about"><About /></section>
        <section id="services"><Services /></section>
        <section id="portfolio"><Portfolio /></section>
        <section id="contact"><Contact /></section>
      </main>
      <footer className="bg-dark text-light py-6 mt-12">
        <div className="container mx-auto text-center text-sm">
          Shyara â€“ Your Digital Marketing & Development Partner. Serving Clients Globally | Based in India.<br />
          <a href="mailto:hello@shyara.in" className="underline">hello@shyara.in</a> | +91-XXXXXXXXXX
        </div>
      </footer>
    </div>
  );
}

export default App;
